import React, { useState } from "react";
import operations from "../../data/operations";
import Button from '@mui/joy/Button';



const ButtonAddOperation = ({operations}) => {

    const [array, setArray] = useState(array);

    const handleClick = () => {
        setArray( operation + {
            description: 'Zakupy w markecie',
            type: 'Food',
            amount: 24.54,
            date: new Date().toLocaleDateString(),
        })
    }

    return (
        <Button color="neutral" onClick={handleClick} />
    )
}

export default ButtonAddOperation;
